mapper.select:


     and robot_task_id = #{robotTaskId}
     and task_name like concat('%', #{taskName}, '%')
     and speech_craft_name_id = #{speechCraftNameId}
     and speech_craft_name like concat('%', #{speechCraftName}, '%')
     and robot_task_status = #{robotTaskStatus}
     and org_id = #{orgId}
     and org_name like concat('%', #{orgName}, '%')
     and robot = #{robot}
     and call_total_count = #{callTotalCount}
     and call_done_count = #{callDoneCount}
     and call_called_count = #{callCalledCount}
     and call_rejected_count = #{callRejectedCount}
     and call_unavailable_count = #{callUnavailableCount}
     and call_from_unavailable_count = #{callFromUnavailableCount}
     and call_busy_count = #{callBusyCount}
     and call_miss_count = #{callMissCount}

        and create_time >= #{startCreateTime}


        and create_time <= #{endCreateTime}


        and task_start_time >= #{startTaskStartTime}


        and task_start_time <= #{endTaskStartTime}


        and task_end_time >= #{startTaskEndTime}


        and task_end_time <= #{endTaskEndTime}

     and phone_num = #{phoneNum}

ORDER BY create_time DESC

