mapper.select:
SELECT (@a :=@a + 1) as sque, re.case_no, ta.custom_name as customName, ta.certificate_no, ta.enter_coll_date AS enterCollDate, ta.arrears_total AS arrearsTotal, ta.close_case_yhje AS closeCaseYhje, ta.overdue_days AS overdueDays, re.contact_name, re.contact_relation, re.phone, (SELECT su.user_name FROM sys_user su WHERE re.create_by = su.user_id) AS agent_name, re.call_sign as callSignEn, CONCAT(re.call_sign,'(',re.call_result,')') AS call_sign, re.find_date, re.remark, re.create_time, re.call_start_time, re.call_end_time, re.call_len / 1000 AS call_len, re.call_radio_location, du.product_name, du.back_case_date, du.recommend_vendor, '华道数据' AS wbjb, '电催' AS csdz, re.call_result FROM t_lc_call_record re, t_lc_task ta, t_lc_duncase du,(SELECT @a := 0) t1 WHERE re.case_no = ta.case_no and re.case_no = du.case_no and ta.task_status != 3 and ta.import_batch_no = du.import_batch_no AND ta.org_id = #{orgId}
AND re.create_time >= #{startCreateTime}
AND re.create_time <= #{endCreateTime}
AND re.call_start_time >= #{startCallStartTime}
AND re.call_start_time <= #{endCallStartTime}
AND re.call_len >= #{startCallLen}
AND re.call_len <= #{endCallLen}
AND re.case_no = #{caseNo}
order by re.create_time desc

mapper.update:
update t_lc_call_record
call_start_time = #{callStartTime}, call_end_time = #{callEndTime}, call_len = #{callLen}, call_sign = #{callSign}, call_result = #{callResult}, call_radio_location = #{callRadioLocation}, call_radio = #{callRadio}, find_date = #{findDate}, remark = #{remark},
where id = #{id}

mapper.select:
SELECT DISTINCT t.case_no,tt.org_name,tt.org_id,u.user_name,u.login_name,
t.id,
t.contact_name,
t.phone,
t.contact_relation,
t.call_start_time,
t.call_end_time,
t.call_len/1000 as call_len,
t.call_sign,
t.call_result,
t.call_radio_location,
t.call_radio,
t.remark,
t.create_time,
t.create_by,
t.find_date,
t.type,
t.platform,
t.star
from t_lc_call_record t,t_lc_duncase tt,sys_user u

    t.case_no=tt.case_no and t.create_by = u.user_id
    and t.call_radio_location is not null
    and t.case_no = #{caseNo}
    and tt.org_id = #{orgId}
    and t.contact_name like concat('%', #{contactName},'%')
    and t.phone = #{phone}
    and t.contact_relation = #{contactRelation}
    and t.call_sign = #{callSign}
    and t.call_result = #{callResult}
    and t.platform = #{platform}
    and t.call_start_time >= #{callStartTime}
    and t.call_start_time <= #{callEndTime}
    and t.call_len >= #{startCallLen}
    and t.call_len <= #{endCallLen}
    and u.user_name like concat('%', #{createName},'%')

order by t.call_start_time desc

SELECT r.id,r.case_no,r.platform,r.call_radio_location,t.org_id,t.org_name FROM t_lc_call_record r,t_lc_task t WHERE r.case_no = t.case_no
AND r.call_radio_location is NOT NULL AND r.call_radio_location != '' AND r.platform IS NOT NULL
AND t.task_status = 2


    AND r.create_time >= #{startCreateTime}


    AND r.create_time <= #{endCreateTime}

ORDER BY r.create_time ASC

select id, contact_name, phone, contact_relation,  call_start_time, call_end_time,call_len, call_sign, call_result,call_radio_location,call_radio,create_time,
create_by,case_no,platform,find_date,agent_name,star,type from t_lc_call_record
where 1 = 1

    AND create_time >= #{createTime}

order by id desc
limit #{pnum},#{psize}

select count(1) from t_lc_call_record
where 1 = 1

    AND create_time >= #{createTime}
