mapper.select:
SELECT
  count(*)
FROM
t_lc_call_record re
WHERE
re.org_id = #{orgId}

    AND re.create_time >= #{startCreateTime}


    AND re.create_time <= #{endCreateTime}


    AND re.call_start_time >= #{startCallStartTime}


    AND re.call_start_time <= #{endCallStartTime}


    AND re.call_len >= #{startCallLen}


    AND re.call_len <= #{endCallLen}


    AND re.case_no = #{caseNo}

order by re.create_time desc

select (@a :=@a + 1) as sque,tab.* from ( SELECT distinct re.case_no, cap.cur_name as customName, cap.certificate_no, cap.rcr AS enterCollDate, cap.rmb_ye AS arrearsTotal, cap.wa_ye AS closeCaseYhje, cap.overdue_days AS overdueDays, cap.tj_wd AS tjWd, cap.area_center AS areaCenter, re.contact_name, re.contact_relation, re.phone, (SELECT su.user_name FROM sys_user su WHERE re.create_by = su.user_id) AS agent_name, re.call_sign as callSignEn, CONCAT(re.call_sign,'(',re.call_result,')') AS call_sign, re.find_date, re.remark, re.create_time, re.call_start_time, re.call_end_time, re.call_len / 1000 AS call_len, re.call_radio_location, cap.cpmc as product_name, cap.tar as back_case_date, cap.tj_firm as recommend_vendor, '华道数据' AS wbjb, '电催' AS csdz, re.call_result, re.cnje FROM t_lc_call_record re, cur_assets_package cap WHERE re.case_no = cap.org_casNo AND cap.org_id = #{orgId}
AND re.create_time >= #{startCreateTime}
AND re.create_time <= #{endCreateTime}
AND re.call_start_time >= #{startCallStartTime}
AND re.call_start_time <= #{endCallStartTime}
AND re.call_len >= #{startCallLen}
AND re.call_len <= #{endCallLen}
AND re.case_no = #{caseNo}
AND cap.cur_name = #{customName}
order by re.create_time desc
limit #{pNum},#{pSize}
) tab,(SELECT @a := 0) t1

SELECT (@a :=@a + 1) as sque, re.case_no, (select cap.cur_name from cur_assets_package cap where re.case_no = cap.org_casNo limit 1) as customName, (select cap.rcr from cur_assets_package cap where re.case_no = cap.org_casNo limit 1) AS enterCollDate, re.contact_name, re.contact_relation, re.phone, re.call_sign as callSignEn, CONCAT(re.call_sign,'(',re.call_result,')') AS call_sign, re.find_date, re.remark, re.create_time, re.call_start_time, re.call_end_time, re.call_len / 1000 AS call_len, re.call_radio_location, (select cap.cpmc from cur_assets_package cap where re.case_no = cap.org_casNo limit 1) as product_name, (select cap.tar from cur_assets_package cap where re.case_no = cap.org_casNo limit 1) as back_case_date, (select cap.tj_firm from cur_assets_package cap where re.case_no = cap.org_casNo limit 1) as recommend_vendor, '华道数据' AS wbjb, '电催' AS csdz, re.call_result, re.cnje FROM t_lc_call_record re,(SELECT @a := 0) t1 WHERE re.org_id = #{orgId}
AND re.create_time >= #{startCreateTime}
AND re.create_time <= #{endCreateTime}
AND re.call_start_time >= #{startCallStartTime}
AND re.call_start_time <= #{endCallStartTime}
AND re.call_len >= #{startCallLen}
AND re.call_len <= #{endCallLen}
AND re.case_no = #{caseNo}
AND cap.cur_name = #{customName}
order by re.create_time desc
limit #{pNum},#{pSize}

mapper.update:
update t_lc_call_record
call_start_time = #{callStartTime}, call_end_time = #{callEndTime}, call_len = #{callLen}, call_sign = #{callSign}, call_result = #{callResult}, call_radio_location = #{callRadioLocation}, call_radio = #{callRadio}, find_date = #{findDate}, remark = #{remark}, action_code = #{actionCode},
where id = #{id}

mapper.select:
SELECT distinct t.case_no,tt.org_name,tt.org_id, t.agent_name as user_name, (select u.login_name from sys_user u where u.user_id = t.create_by) as login_name, t.id, t.contact_name, t.phone, t.contact_relation, t.call_start_time, t.call_end_time, t.call_len/1000 as call_len, t.call_sign, t.call_result, t.call_radio_location, t.call_radio, t.remark, t.create_time, t.create_by, t.find_date, t.type, t.platform, t.star from t_lc_call_record t,t_lc_task tt
t.case_no=tt.case_no and t.call_radio_location is not null and t.case_no = #{caseNo} and tt.org_id = #{orgId} and t.contact_name like concat('%', #{contactName},'%') and t.phone = #{phone} and t.contact_relation = #{contactRelation} and t.call_sign = #{callSign} and t.call_result = #{callResult} and t.platform = #{platform} and t.call_start_time >= #{callStartTime} and t.call_start_time <= #{callEndTime} and t.call_len >= #{startCallLen} and t.agent_name like concat('%', #{createName},'%') and t.call_len <= #{endCallLen}
order by t.call_start_time desc

SELECT r.id,r.case_no,r.platform,r.call_radio_location,t.org_id,t.org_name FROM t_lc_call_record r,t_lc_task t WHERE r.case_no = t.case_no
AND r.call_radio_location is NOT NULL AND r.call_radio_location != '' AND r.platform IS NOT NULL
AND t.task_status = 2


    AND r.create_time >= #{startCreateTime}


    AND r.create_time <= #{endCreateTime}

ORDER BY r.create_time ASC

select id, contact_name, phone, contact_relation,  call_start_time, call_end_time,call_len, call_sign, call_result,call_radio_location,call_radio,create_time,
create_by,case_no,platform,find_date,agent_name,star,type from t_lc_call_record
where 1 = 1

    AND create_time >= #{createTime}

order by id desc
limit #{pnum},#{psize}

select count(1) from t_lc_call_record
where 1 = 1

    AND create_time >= #{createTime}
