mapper.update:
update t_lc_collectionrecordimprot
org_caseNo = #{orgCaseno}, certificate_no = #{certificateNo}, phone = #{phone}, relation = #{relation}, phone_code = #{phoneCode}, remark = #{remark}, seat = #{seat}, call_start_time = #{callStartTime}, call_end_time = #{callEndTime}, call_length = #{callLength}, call_record_id = #{callRecordId}, grade = #{grade}, name = #{name}, call_status = #{callStatus}, org_id = #{orgId}, import_batch_no = #{importBatchNo}, create_by = #{createBy}, create_id = #{createId}, create_time = #{createTime},
where id = #{id}

mapper.select:


     and org_caseNo = #{orgCaseNo}
     and certificate_no = #{certificateNo}
     and phone = #{phone}
     and relation = #{relation}

     and remark like concat('%', #{remark}, '%')
     and seat = #{seat}
     and call_start_time = #{callStartTime}
     and call_end_time = #{callEndTime}
     and call_length = #{callLength}
     and call_record_id = #{callRecordId}
     and org_id = #{orgId}
     and import_batch_no = #{importBatchNo}
     and create_by = #{createBy}
     and create_time >= #{startDate}
     and create_time <= #{endDate}
     and is_exception = #{isException}

order by create_time desc

select count(*) as total FROM t_lc_collectionrecordimprot_temp

     and org_id = #{orgId}
     and import_batch_no = #{importBatchNo}
     and is_exception = #{isException}

        select t.org_caseNo as orgCaseNo,t.id
        from t_lc_collectionrecordimprot_temp t WHERE t.import_batch_no = #{importBatchNo} and NOT EXISTS(
	      SELECT case_no from t_lc_duncase tt WHERE tt.case_no = t.org_caseNo and tt.org_id = t.org_id)

SELECT m.importBatchNo,m.orgId,m.total,m.createBy,m.createTime,n.org_name as orgName
from (SELECT import_batch_no as importBatchNo,
            max(org_id)as orgId ,
            count(id) as total,
            max(create_by)as createBy,
            max(create_time)as createTime
      from t_lc_collectionrecordimprot_temp
      where is_exception = '0'
      GROUP BY import_batch_no) m
LEFT JOIN org_package n on m.orgId = n.dept_id

     and m.importBatchNo = #{importBatchNo}
     and m.orgId = #{orgId}
     and m.createTime >=  #{startDate}
     and m.createTime <=  #{endDate}

select id, import_batch_no, org_id, org_name, total, create_by, create_time from t_lc_collectionrecordimprot_flow

     and import_batch_no = #{importBatchNo}
     and org_id = #{orgId}
     and create_time >=  #{startDate}
     and create_time <=  #{endDate}
